import React from 'react'

class AddFishForm extends React.Component {
    nameRef = React.createRef();
    priceRef = React.createRef();
    statusRef = React.createRef();
    descRef = React.createRef();
    imageRef =React.createRef();
    createFish= event => {

        // 1. Stop form from submitting
        event.preventDefault();
        const fish = {
            name: this.nameRef.current,
            price: this.priceRef.current,
            status: this.statusRef.current,
            desc: this.descRef.current,
            image: this.imageRef.current,
        }
        console.log(this);
    }
    render () {
    return (
            <form class="fish-edit" onSubmit={this.createFish}>
                <input name="name"  ref={this.nameRef} type="text" placeholder="Name" />
                <input name="price" ref={this.priceRef}type="text" placeholder="Price" />
                <select name="status" ref={this.statusRef} >
                    <option value="available">Fresh</option>
                    <option value="unavailable">Sold Out!</option>
                </select>
                <input name="desc" ref={this.descRef} placeholder="Desc" />
                <input name="image" ref={this.imageRef} type="text" placeholder="Image" />
                <button type="submit">+Add Fish</button>
            </form>
    )
    }
}

export default AddFishForm;
